import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Card, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap'
import { hienThiCacKhuNha } from '../../../actions/khuNhaActions';
import { hienThiCacPhong } from '../../../actions/phongAction';

class DanhSachPhongTrong extends Component {
    constructor(props) {
        super(props);

        this.state = {
            phong: ""
        }
    }

    componentDidMount = () => {
        this.props.hienThiCacKhuNha();
        this.props.hienThiCacPhong();
    }

    clickPhong = (phong) => {
        if (this.state.phong) {
            this.setState({
                phong: ""
            });
        } else {
            this.setState({
                phong: phong
            });
        }
    }

    render() {
        return (
            <div className="row" style={{ display: "block", clear: "both" }}>
                {this.props.khuNha.KhuNhas.length > 0 ? this.props.khuNha.KhuNhas.map(
                    item =>
                        <div>
                            <center className="ten-khu-nha">{item.tenKhuNha}</center>
                            <div className="row">
                                {this.props.phong.Phongs.length > 0 ? this.props.phong.Phongs.filter(
                                    e => (e.KhuNha.khuNhaId == item.khuNhaId) && (e.loaiPhong == localStorage.getItem("gioiTinh"))
                                ).map(
                                    pItem =>
                                        <div className="col-md-3" onClick={() => this.clickPhong(pItem)}>
                                            <Card className="ten-phong"><center>{pItem.tenPhong}</center></Card>
                                        </div>
                                ) : null}
                            </div>
                        </div>
                ) : null}
                <Modal isOpen={this.state.phong}>
                    <ModalHeader>
                        <b>{this.state.phong.tenPhong}</b>
                    </ModalHeader>
                </Modal>
            </div>
        )
    }
}

const mapStatetoProps = state => {
    return {
        khuNha: state.khuNha,
        phong: state.phong
    }
}

const mapActiontoProps = dispatch => {
    return {
        hienThiCacKhuNha: () => dispatch(hienThiCacKhuNha()),
        hienThiCacPhong: () => dispatch(hienThiCacPhong())
    }
}

export default connect(mapStatetoProps, mapActiontoProps)(DanhSachPhongTrong);
