import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Card, Modal, ModalHeader, ModalBody, ModalFooter, Col, Row } from 'reactstrap'
import { hienThiCacKhuNha } from '../../../actions/khuNhaActions';
import { hienThiCacPhong } from '../../../actions/phongAction';

class DanhSachPhongTrong extends Component {
    constructor(props) {
        super(props);

        this.state = {
            phong: ""
        }
    }

    componentDidMount = () => {
        this.props.hienThiCacKhuNha();
        this.props.hienThiCacPhong();
    }

    clickPhong = (phong) => {
        if (this.state.phong) {
            this.setState({
                phong: ""
            });
        } else {
            this.setState({
                phong: phong
            });
        }
    }

    render() {
        return (
            <div className="row" style={{ display: "block", clear: "both" }}>
                {this.props.khuNha.KhuNhas.length > 0 ? this.props.khuNha.KhuNhas.map(
                    item =>
                        <div>
                            <center className="ten-khu-nha">{item.tenKhuNha}</center>
                            <div className="row">
                                {this.props.phong.Phongs.length > 0 ? this.props.phong.Phongs.filter(
                                    e => (e.KhuNha.khuNhaId == item.khuNhaId)
                                        && (e.loaiPhong == localStorage.getItem("gioiTinh"))
                                        && (e.soSinhVien < e.soSinhVienMax)
                                ).map(
                                    pItem =>
                                        <div className="col-md-3" onClick={() => this.clickPhong(pItem)}>
                                            <Card className="ten-phong"><center>{pItem.tenPhong}</center></Card>
                                        </div>
                                ) : null}
                            </div>
                        </div>
                ) : null}
                <Modal isOpen={this.state.phong}>
                    <ModalHeader>
                        <center>
                            <b>{this.state.phong.tenPhong}</b>
                        </center>
                    </ModalHeader>
                    <ModalBody>
                        <Row>
                            <Col md="6">
                                <p>
                                    <b> Loại phòng: </b>
                                    {this.state.phong.loaiPhong}
                                </p>
                                <p>
                                    <b> Số sinh viên hiện tại: </b>
                                    {this.state.phong.soSinhVien}
                                </p>
                                <p>
                                    <b> Số sinh viên tối đa: </b>
                                    {this.state.phong.soSinhVienMax}
                                </p>
                            </Col>
                            <Col md="6">
                                <p>
                                    <b> Khu: </b>
                                    {/* {this.state.phong} */}
                                </p>
                                {/* <p>
                                <b> Người quản lý: </b>
                                {this.state.phong.KhuNha.nguoiQuanLy}
                            </p>
                            <p>
                                <b> Số điện thoại: </b>
                                {this.state.phong.KhuNha.SDT}
                            </p> */}
                            </Col>
                        </Row>
                    </ModalBody>
                </Modal>
            </div>
        )
    }
}

const mapStatetoProps = state => {
    return {
        khuNha: state.khuNha,
        phong: state.phong
    }
}

const mapActiontoProps = dispatch => {
    return {
        hienThiCacKhuNha: () => dispatch(hienThiCacKhuNha()),
        hienThiCacPhong: () => dispatch(hienThiCacPhong())
    }
}

export default connect(mapStatetoProps, mapActiontoProps)(DanhSachPhongTrong);
