import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Button, Form, FormGroup, Input, Col } from 'reactstrap';
import khuNha_icon from '../../../images/admin/khuNha_icon.png';
//import * from '../../../actions/khuNhaActions';
import '../../../style/khuNha.css';
import '../../../style/style1.css';
import '../../../style/style_common.css';

export default class KhuNha extends Component {
    constructor(props) {
        super(props);

        this.state = {
            isEdit: false
        }
    }

    clickEdit = () => {
        this.setState({
            isEdit: !this.state.isEdit
        });
    }

    render() {
        while(1){
            if ( !this.state.isEdit ) {
                console.log(this.state.isEdit);
                return (
                    <div className="col-md-3">
                        <div className="khuNha_item view view-first" >
                            <img className="khuNhaImage" src={khuNha_icon} alt="KHUNHA" />
                            <center className="tenKhuNha"><b>{this.props.tenKhuNha}</b></center>
                            <div className="mask">
                                <Button outline color="primary" onClick="/:khuNhaid/danhsachphong/"><b>Danh sách phòng</b></Button>
                                <Button outline color="primary" onClick={this.clickEdit}><b>Chỉnh sửa</b></Button>
                                <Button outline color="primary" onClick=""><b>Xóa</b></Button>
                            </div>

                        </div>
                    </div>
                );
            } else {
                console.log(this.state.isEdit);
                return (

                    <div className="col-md-3">
                        <Form>
                            <FormGroup>
                                <Col sm="12" md={{ size: 12 }}>
                                    <i>Tên mới:</i>
                                    <Input
                                        type="text"
                                        id="Content"
                                        name="Content"
                                        value={this.state.name}
                                        onChange={this.handleChangeName}
                                    />
                                </Col>
                            </FormGroup>
                            <center>
                                <Button outline color="primary" onClick={this.clickEdit}>Thoát</Button>
                                    {/* <Button outline color="primary" onClick={this.state.isEdit}>Cancel</Button> */}
                                <Button type="submit" outline color="primary" onClick="">Chỉnh sửa</Button>
                            </center>
                        </Form>
                    </div>
                );        
            }
        }        
    }
}

