import React, { Component } from 'react';
import Header from './header';
import Menu from './menu';
import DanhSachKhuNha from './khuNha/danhSachKhuNha';
import ThemKhuNha from './khuNha/themKhuNha';

export default class AdminHome extends Component {
    constructor(props) {
        super(props);
        this.child = React.createRef();
    }

    themKhuNha = () => {
        console.log(this.child);
        // this.child.current.themKhuNhaMoi(tenKhuNha)
        this.child.current.getAlert();
    }

    render() {
        return (
            <div>
                <Header />
                <div className="row">
                    <Menu />

                    <div className="col-md-9">
                        <div className="row">
                            <DanhSachKhuNha forwardRef={this.child} />
                            {/* <ThemKhuNha themKhuNha={this.themKhuNha}/> */}

                        <button onClick={this.ThemKhuNha}>Ahihi</button>  

                        </div>
                    </div>
                </div>
            </div> 
        );
    }
}
