import React, { Component } from 'react';
import { Button } from 'reactstrap';
import { connect } from 'react-redux';
import { hienThiCacKhuNha } from '../../../actions/khuNhaActions';
import '../../../style/khuNha.css'

class KhuNhaList extends Component {
    constructor(props) {
        super(props);
        this.state = {
            id: "",
            tenKhuNha: "",
            diaChi: "",
            quanLyKhuNha: "",
            SDT: "",
            openAddModal: false
        }
    }

    componentDidMount = () => {
        this.props.hienThiCacKhuNha();
    }

    render() {
        return (
            <div className="row khu-nha">
                <div className="add">
                    <button>+ Thêm khu nhà</button>
                </div>

                <div>
                    <table className="table">
                        <tr>
                            <th>STT</th>
                            <th>Tên khu nhà</th>
                            <th>Địa chỉ</th>
                            <th>Người quản lý</th>
                            <th>Số điện thoại</th>
                            <th>Chỉnh sửa</th>
                            <th>Xóa</th>
                        </tr>

                        {this.props.khuNha.KhuNhas ? this.props.khuNha.KhuNhas.map(
                            (item, index) =>
                                <tr>
                                    <td>{index + 1}</td>
                                    <td>Khu {item.tenKhuNha}</td>
                                    <td>{item.diaChi}</td>
                                    <td>{item.quanLyKhuNha}</td>
                                    <td>{item.SDT}</td>
                                    <td>
                                        <span>
                                            <i className="far fa-edit"></i>
                                        </span>
                                    </td>
                                    <td>
                                        <span>
                                            <i className="fas fa-trash-alt"></i>
                                        </span>
                                    </td>
                                </tr>
                        ): null}

                    </table>
                </div>
            </div>
        )
    }
}

const mapStatetoProps = state => {
    return {
        khuNha: state.khuNha
    }
}

const mapActiontoProps = dispatch => {
    return {
        hienThiCacKhuNha: () => dispatch(hienThiCacKhuNha())
    }
}

export default connect(mapStatetoProps, mapActiontoProps)(KhuNhaList);
