import React, { Component } from 'react';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter, Form, Input } from 'reactstrap';
import { connect } from 'react-redux';
import { hienThiCacKhuNha } from '../../../actions/khuNhaActions';
import '../../../style/khuNha.css';

class KhuNhaList extends Component {
    constructor(props) {
        super(props);
        this.state = {
            id: "",
            tenKhuNha: "",
            diaChi: "",
            quanLyKhuNha: "",
            SDT: "",
            openAddModal: false,
            themMoi: false
        }
    }

    componentDidMount = () => {
        this.props.hienThiCacKhuNha();
    }

    openModal = () => {
        if ( !this.state.openAddModal )
            this.setState({
                openAddModal: !this.state.openAddModal,
                themMoi: true      
            })
        else {
            this.setState({
                openAddModal: !this.state.openAddModal,
                themMoi: false
            })
        }
    }

    suaThongTin = () => {
        this.setState({
            // tenKhuNha: tenKhuNha,
            // diaChi: diaChi,
            // quanLyKhuNha: quanLyKhuNha,
            // SDT: SDT,
            openAddModal: true
        })
    }

    render() {
        return (
            <div className="row khu-nha">
                <div>
                    <Button className="add" onClick={this.openModal} style={{cursor : "pointer"}}>
                        <b>+ Thêm khu nhà</b>
                    </Button>
                </div>

                <div>
                    <table className="table">
                        <tr>
                            <th>STT</th>
                            <th>Tên khu nhà</th>
                            <th>Địa chỉ</th>
                            <th>Người quản lý</th>
                            <th>Số điện thoại</th>
                            <th>Chỉnh sửa</th>
                            <th>Xóa</th>
                        </tr>

                        {this.props.khuNha.KhuNhas ? this.props.khuNha.KhuNhas.map(
                            (item, index) =>
                                <tr>
                                    <td>{index + 1}</td>
                                    <td>Khu {item.tenKhuNha}</td>
                                    <td>{item.diaChi}</td>
                                    <td>{item.quanLyKhuNha}</td>
                                    <td>{item.SDT}</td>
                                    <td onClick={this.suaThongTin}>
                                        <span onClick={this.suaThongTin}>
                                            <i className="far fa-edit"></i>
                                        </span>
                                    </td>
                                    <td>
                                        <span>
                                            <i className="fas fa-trash-alt"></i>
                                        </span>
                                    </td>
                                </tr>
                        ): null}
                    </table>

                    <Modal isOpen={this.state.openAddModal} >
                        <ModalHeader>
                            {this.state.themMoi ? 
                            <b>Thêm một khu nhà mới</b>
                            :
                            <b>Sửa thông tin khu nhà</b>
                            }
                        </ModalHeader>
                        <ModalBody>
                            <Form>
                                <i>Tên khu nhà:</i>
                                <Input type="text" 
                                       value={this.state.tenKhuNha} 
                                       onChange={this.thayDoiTayDoiTenKhuNha}>
                                </Input>
                                <br />
                                <i>Địa chỉ:</i>
                                <Input type="text"
                                       value={this.state.diaChi}
                                       onChange={thayDoiDiaChi}
                                ></Input>
                                <br />
                                <i>Người quản lý:</i>
                                <Input type="text"
                                       value={this.state.quanLyKhuNha}
                                       onChange={thayDoiNguoiQuanLy}
                                ></Input>
                                <br />
                                <i>Số điện thại:</i>
                                <Input type="text"
                                       value={this.state.SDT}
                                       onChange={this.state.SDT}
                                ></Input>
                            </Form>
                        </ModalBody>
                        <ModalFooter>
                            <Button outline color ="primary" onClick={this.openModal}>Cancel</Button>
                            <Button outline color ="primary">Ok</Button>
                        </ModalFooter>
                    </Modal>
                </div>
            </div>
        )
    }
}

const mapStatetoProps = state => {
    return {
        khuNha: state.khuNha
    }
}

const mapActiontoProps = dispatch => {
    return {
        hienThiCacKhuNha: () => dispatch(hienThiCacKhuNha())
    }
}

export default connect(mapStatetoProps, mapActiontoProps)(KhuNhaList);
